-- init.sql
-- PostgreSQL initialization script for Docker
-- Place this file in: ./init.sql → mounted to /docker-entrypoint-initdb.d/init.sql

SET statement_timeout = 0;
SET lock_timeout = 0;
SET idle_in_transaction_session_timeout = 0;
SET client_encoding = 'UTF8';
SET standard_conforming_strings = on;
SELECT pg_catalog.set_config('search_path', '', false);
SET check_function_bodies = false;
SET xmloption = content;
SET client_min_messages = warning;
SET row_security = off;

-- Enable unaccent for text search
CREATE EXTENSION IF NOT EXISTS unaccent WITH SCHEMA public;
COMMENT ON EXTENSION unaccent IS 'text search dictionary that removes accents';

SET default_tablespace = '';
SET default_table_access_method = heap;

-- ========================================
-- TABLES
-- ========================================

CREATE TABLE public.brands (
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY (
        SEQUENCE NAME public.brands_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1
    ),
    created_at timestamp(6) with time zone NOT NULL,
    image_url character varying(255),
    is_active boolean NOT NULL,
    name character varying(100) NOT NULL,
    slug character varying(100) NOT NULL,
    is_featured boolean DEFAULT false NOT NULL,
    description text
);

CREATE TABLE public.categories (
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY (
        SEQUENCE NAME public.categories_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1
    ),
    created_at timestamp(6) with time zone NOT NULL,
    image_url character varying(255),
    is_active boolean NOT NULL,
    name character varying(100) NOT NULL,
    slug character varying(100) NOT NULL
);

CREATE TABLE public.product_reviews (
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY (
        SEQUENCE NAME public.product_reviews_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1
    ),
    content text,
    created_at timestamp(6) with time zone,
    customer_id bigint NOT NULL,
    images jsonb,
    is_approved boolean,
    order_id bigint NOT NULL,
    product_id bigint NOT NULL,
    rating integer NOT NULL,
    updated_at timestamp(6) with time zone,
    username character varying(255) NOT NULL,
    variant_id bigint NOT NULL
);

CREATE TABLE public.product_variants (
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY (
        SEQUENCE NAME public.product_variants_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1
    ),
    attributes jsonb,
    created_at timestamp(6) with time zone,
    discount_percent integer,
    image_urls jsonb,
    import_price numeric(38,2),
    is_active boolean,
    name character varying(255),
    selling_price numeric(38,2) NOT NULL,
    sku character varying(255) NOT NULL,
    updated_at timestamp(6) with time zone,
    product_id bigint NOT NULL,
    sold_count bigint,
    status character varying(255),
    base_price numeric(38,2),
    is_deleted boolean DEFAULT false NOT NULL
);

CREATE TABLE public.products (
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY (
        SEQUENCE NAME public.products_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1
    ),
    created_at timestamp(6) with time zone,
    description text,
    image_url character varying(255),
    is_active boolean,
    is_featured boolean,
    name character varying(255),
    short_description text,
    slug character varying(255) NOT NULL,
    technical_specs jsonb,
    updated_at timestamp(6) with time zone,
    brand_id bigint NOT NULL,
    category_id bigint NOT NULL,
    product_code character varying(255) NOT NULL,
    total_sold bigint,
    main_variant_id bigint,
    is_deleted boolean DEFAULT false NOT NULL,
    rating_avg numeric(3,2),
    rating_sum integer DEFAULT 0 NOT NULL,
    rating_count integer DEFAULT 0 NOT NULL
);

